import * as React from 'react';
import { useState } from 'react';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Popper from '@mui/material/Popper';
import PopupState, { bindToggle, bindPopper } from 'material-ui-popup-state';
import Fade from '@mui/material/Fade';
import Paper from '@mui/material/Paper';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';
import DropdownCard from './dropdownCard';

export default function PopperPopupState() {
    const [style, setStyle] = useState({ display: 'none' });
    return (
        <PopupState variant="popper" popupId="demo-popup-popper">
            {(popupState) => (
                <div onMouseEnter={e => {
                    setStyle({ display: 'block', opacity: 2.7 });
                }}
                    onMouseLeave={e => {
                        setStyle({ display: 'none' })
                    }}>
                    <Button variant="" className='selectButton' {...bindToggle(popupState)}>
                        ALL CATEGORIES <ArrowDropDownIcon />
                    </Button>
                    <Popper style={style} sx={{margin}} {...bindPopper(popupState)} transition>
                        {({ TransitionProps }) => (
                            <Fade {...TransitionProps} timeout={350}>
                                <Paper>
                                    <DropdownCard />
                                </Paper>
                            </Fade>
                        )}
                    </Popper>
                </div>
            )}
        </PopupState>
    );
}
